package week1.day1.Assignments;

public class ConvertNegativeNum {
	
	//convert a negative number to a positive number
	
	
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = -40;
		if(number<0)
			number=number*-1;
		System.out.println(number);

	}

}
